package com.tvd12.example.aes;

import com.tvd12.ezyfox.security.EzyAsyCrypt;
import com.tvd12.ezyfox.security.EzyBase64;

public class BodRsaSessionKeyDecrypt {

    public static void main(String[] args) throws Exception {
        EzyAsyCrypt ezyAsyCrypt = EzyAsyCrypt.builder()
            .privateKey(EzyBase64.decode(""))
            .build();
        String encryptedKey = "";
        byte[] encryptedText = ezyAsyCrypt.decrypt(EzyBase64.decode(encryptedKey));
        System.out.println(EzyBase64.encode2utf(encryptedText));
    }
}
